package com.Anicoboard.Javatutorials.Array;

public class DefineDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] classGroup = { "leos", "days", "mays", "egos" };
		
		System.out.println(classGroup[0]); // array index 는 0부터 시작.
		System.out.println(classGroup[1]);
		System.out.println(classGroup[2]);
		System.out.println(classGroup[3]);
				
		
		int[] classNumber = { 1, 2, 3, 4 };
		
		System.out.println(classNumber[0]);
		System.out.println(classNumber[1]);
		System.out.println(classNumber[2]);
		System.out.println(classNumber[3]);
		
		

	}

}
