package com.Anicoboard.Javatutorials.Array;

public class LengthDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] classGroup = new String[4];
		
        classGroup[0] = "leos";
        System.out.println(classGroup.length);
        
        classGroup[1] = "days";
        System.out.println(classGroup.length);
        
        classGroup[2] = "mays";
        System.out.println(classGroup.length);
        
        classGroup[3] = "gays";
        System.out.println(classGroup.length);
        

	}

}
